import java.util.Scanner;
public class exercicewhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int nombre1 = 3;
		int nombre2 = 5;
		System.out.print("");
		int resultat = 00000000000;
		while(resultat != nombre1 + nombre2)
		{	
			
			System.out.print("Combien font "+ nombre1 + " + " + nombre2 + " : " );
			resultat = sc.nextInt();
		}	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	}

}
